from PIL import Image



window_size = 128
threshold_percent = 60

output_path = "/home/lab/Tumor_Detection/classification/cropped_region"

with open("../tumorAnnotation.json", "r") as file:
    data = json.load(file)

keys_pos = ["POSITIVE_TEST_YS", "POSITIVE_TEST_TCGA", "POSITIVE_TEST_LUAC", "POSITIVE_TEST_SSSF"]
keys_neg = ["NEGATIVE_TEST_LUAC", "NEGATIVE_TEST_SSSF"]

for key in keys_pos:
    current_list = data.get(key, [])
    for value in current_list:
        value = value.split('/')[-1]
        crop_region(value, "pos")


for key in keys_neg:
    current_list = data.get(key, [])
    for value in current_list:
        value = value.split('/')[-1]
        crop_region(value, "neg")




def crop_region(tumor_name, pos_or_neg):
    # 사진 하나 찝어서 가져왔다고 가정
    
    model.eval()
    transform = transforms.ToTensor()
    sigmoid = nn.Sigmoid()

    output_path = os.path.join(output_path, pos_and_neg + "_" + tumor_name)
    
    # CLAM 이미지 가져오기
    CLAM_image = Image.open(os.path.join(base_path, tumor_name, f"{tumor_name}_masked.jpg"))
    # heatmap 이미지 가져오기
    heatmap_image = Image.open(os.path.join(heatmap_base_path, pos_or_neg + "_" + tumor_name + ".jpg"))
    # 원본 svs이미지 가져오기
    svs_path = os.path.join(svs_base_path, tumor_name + ".svs")
    svs_image = openslide.open_slide(svs_path)

    print(CLAM_image.size)
    print(heatmap_image.size)

    width, height = CLAM_image.size
    
    # CLAM기준 가로, 세로의 patch개수 구하기
    width, height = int(width)/32, int(height)/32

    with torch.no_grad():
        for x in range(width-3):
            for y in range(height-3):
                total = 0
                wrong = 0
                for path in os.listdir(patch_path):
                    if patch.endswith(".png"):
                        pattern = r"_(\d+)_(\d+)\.png"
                        matches = re.findall(pattern, patch)
                        patch_w, patch_h = matches[0]
                        patch_w, patch_h = int(patch_w)/8, int(patch_h)/8

                        if patch_w > x and patch_w < x + 4:
                            total += 1
                            patch = Image.open(os.path.join(patch_path, patch))
                            image = transform(patch).to(device).unsqueeze(dim=0)

                            output = model(image)
                            probs_neg = sigmoid(output)[0][0].item()
                            probs_pos = sigmoid(output)[0][0].item()

                            if pos_or_neg == "pos":
                                if probs_neg >= 60:
                                    wrong += 1
                print(x, y, total, wrong)
                acc = wrong / total * 100.
                if acc <= 90:
                    crop(CLAM_image, x, y)
                    crop(heatmap_image, x, y)
                    crop_svs(svs_image, x, y)
        print("Done")
                            
                        


def crop(image, x, y, name):
    cropped_image = image.crop((x*32, y*32, x*32 + window_size, y*32 + window_size))
    output_path = os.path.join(output_path + name + ".jpeg")
    cropped_image.save(output_path)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        

def crop_svs(image, x, y):
    level = 0
    x = x * 32 * 8
    y = y * 32 * 8
    window_size * 8
    region = image.read_region((x, y), level, (window_size, window_size))
    region = region.convert("RGB")
    output_path = os.path.join(output_path + "origin.jpeg")
    region.save(output_path, "JPEG")